    return callApi('PATCH', `/apps/${APP_IDS.KUNDENDATEN}/records/${id}`, { fields });
  }
  static async deleteKundendatenEntry(id: string) {
    return callApi('DELETE', `/apps/${APP_IDS.KUNDENDATEN}/records/${id}`);
  }

