                    <p className="text-muted-foreground">Keine weiteren Termine in den nächsten 7 Tagen</p>
                  </CardContent>
                </Card>
              ) : (
                <div className="space-y-6">
                  {upcomingByDate.map(([dateKey, apts]) => (
                    <div key={dateKey}>
                      <div className="sticky top-0 bg-background py-2 z-10">
