     1→import { useState, useEffect, useMemo } from 'react';
     2→import { format, parseISO, isToday, isBefore, startOfWeek, endOfWeek, startOfMonth, endOfMonth, addDays, isWithinInterval } from 'date-fns';
     3→import { de } from 'date-fns/locale';
     4→import { Calendar, Clock, Users, Euro, Inbox, Plus, Phone, Mail, MapPin, FileText } from 'lucide-react';
     5→import type { Terminanfrage, Leistungskatalog, Kundendaten } from '@/types/app';
     6→import { LivingAppsService, extractRecordId } from '@/services/livingAppsService';
     7→
     8→import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
     9→import { Button } from '@/components/ui/button';
    10→import { Badge } from '@/components/ui/badge';
    11→import { Skeleton } from '@/components/ui/skeleton';
    12→import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger, DialogFooter, DialogDescription } from '@/components/ui/dialog';
    13→import { Sheet, SheetContent, SheetHeader, SheetTitle, SheetDescription } from '@/components/ui/sheet';
    14→import { Input } from '@/components/ui/input';
    15→import { Label } from '@/components/ui/label';
    16→import { Textarea } from '@/components/ui/textarea';
    17→import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
    18→import { ScrollArea, ScrollAreaViewport } from '@/components/ui/scroll-area';
    19→
    20→// Duration mapping for display
    21→const DURATION_MAP: Record<string, number> = {
    22→  'dauer_30': 30,
    23→  'dauer_45': 45,
    24→  'dauer_60': 60,
    25→};
    26→
    27→// Appointment with resolved service data
    28→interface EnrichedAppointment extends Terminanfrage {
    29→  serviceName?: string;
    30→  servicePrice?: number;
    31→}
    32→
    33→// Form data for new appointment
    34→interface NewAppointmentForm {
    35→  kunde_vorname: string;
    36→  kunde_nachname: string;
    37→  kunde_telefon: string;
    38→  e_mail_adresse: string;
    39→  wunschtermin_date: string;
    40→  wunschtermin_time: string;
    41→  massageleistung: string;
    42→  gesamtdauer: string;
    43→  anmerkungen: string;
    44→}
    45→
    46→const initialFormData: NewAppointmentForm = {
    47→  kunde_vorname: '',
    48→  kunde_nachname: '',
    49→  kunde_telefon: '',
    50→  e_mail_adresse: '',
    51→  wunschtermin_date: '',
    52→  wunschtermin_time: '',
    53→  massageleistung: '',
    54→  gesamtdauer: '',
    55→  anmerkungen: '',
    56→};
    57→
    58→export default function Dashboard() {
    59→  const [appointments, setAppointments] = useState<Terminanfrage[]>([]);
    60→  const [services, setServices] = useState<Leistungskatalog[]>([]);
    61→  const [customers, setCustomers] = useState<Kundendaten[]>([]);
    62→  const [loading, setLoading] = useState(true);
    63→  const [error, setError] = useState<Error | null>(null);
    64→
    65→  // Dialog states
    66→  const [createDialogOpen, setCreateDialogOpen] = useState(false);
    67→  const [selectedAppointment, setSelectedAppointment] = useState<EnrichedAppointment | null>(null);
    68→  const [formData, setFormData] = useState<NewAppointmentForm>(initialFormData);
    69→  const [submitting, setSubmitting] = useState(false);
    70→
    71→  // Fetch all data
    72→  useEffect(() => {
    73→    async function fetchData() {
    74→      try {
    75→        setLoading(true);
    76→        const [appointmentsData, servicesData, customersData] = await Promise.all([
    77→          LivingAppsService.getTerminanfrage(),
    78→          LivingAppsService.getLeistungskatalog(),
    79→          LivingAppsService.getKundendaten(),
    80→        ]);
    81→        setAppointments(appointmentsData);
    82→        setServices(servicesData);
    83→        setCustomers(customersData);
    84→      } catch (err) {
    85→        setError(err instanceof Error ? err : new Error('Fehler beim Laden der Daten'));
    86→      } finally {
    87→        setLoading(false);
    88→      }
    89→    }
    90→    fetchData();
    91→  }, []);
    92→
    93→  // Create lookup map for services
    94→  const serviceMap = useMemo(() => {
    95→    const map = new Map<string, Leistungskatalog>();
    96→    services.forEach(s => map.set(s.record_id, s));
    97→    return map;
    98→  }, [services]);
    99→
   100→  // Enrich appointments with service data
   101→  const enrichedAppointments = useMemo(() => {
   102→    return appointments.map(apt => {
   103→      const serviceId = extractRecordId(apt.fields.massageleistung);
   104→      const service = serviceId ? serviceMap.get(serviceId) : null;
   105→      return {
   106→        ...apt,
   107→        serviceName: service?.fields.leistungsname || 'Unbekannte Leistung',
   108→        servicePrice: service?.fields.preis,
   109→      } as EnrichedAppointment;
   110→    });
   111→  }, [appointments, serviceMap]);
   112→
   113→  // Today's appointments
   114→  const todayAppointments = useMemo(() => {
   115→    const today = new Date();
   116→    return enrichedAppointments
   117→      .filter(apt => {
   118→        if (!apt.fields.wunschtermin) return false;
   119→        const aptDate = parseISO(apt.fields.wunschtermin);
   120→        return isToday(aptDate);
   121→      })
   122→      .sort((a, b) => {
   123→        const dateA = a.fields.wunschtermin || '';
   124→        const dateB = b.fields.wunschtermin || '';
   125→        return dateA.localeCompare(dateB);
   126→      });
   127→  }, [enrichedAppointments]);
   128→
   129→  // Upcoming appointments (next 7 days, excluding today)
   130→  const upcomingAppointments = useMemo(() => {
   131→    const today = new Date();
   132→    const weekEnd = addDays(today, 7);
   133→    return enrichedAppointments
   134→      .filter(apt => {
   135→        if (!apt.fields.wunschtermin) return false;
   136→        const aptDate = parseISO(apt.fields.wunschtermin);
   137→        return !isToday(aptDate) && isWithinInterval(aptDate, { start: today, end: weekEnd });
   138→      })
   139→      .sort((a, b) => {
   140→        const dateA = a.fields.wunschtermin || '';
   141→        const dateB = b.fields.wunschtermin || '';
   142→        return dateA.localeCompare(dateB);
   143→      });
   144→  }, [enrichedAppointments]);
   145→
   146→  // Group upcoming by date
   147→  const upcomingByDate = useMemo(() => {
   148→    const groups = new Map<string, EnrichedAppointment[]>();
   149→    upcomingAppointments.forEach(apt => {
   150→      if (!apt.fields.wunschtermin) return;
   151→      const dateKey = apt.fields.wunschtermin.split('T')[0];
   152→      if (!groups.has(dateKey)) {
   153→        groups.set(dateKey, []);
   154→      }
   155→      groups.get(dateKey)!.push(apt);
   156→    });
   157→    return Array.from(groups.entries()).sort((a, b) => a[0].localeCompare(b[0]));
   158→  }, [upcomingAppointments]);
   159→
   160→  // Stats calculations
   161→  const stats = useMemo(() => {
   162→    const now = new Date();
   163→    const weekStart = startOfWeek(now, { locale: de });
   164→    const weekEnd = endOfWeek(now, { locale: de });
   165→    const monthStart = startOfMonth(now);
   166→    const monthEnd = endOfMonth(now);
   167→
   168→    // This week's appointments
   169→    const thisWeekCount = enrichedAppointments.filter(apt => {
   170→      if (!apt.fields.wunschtermin) return false;
   171→      const aptDate = parseISO(apt.fields.wunschtermin);
   172→      return isWithinInterval(aptDate, { start: weekStart, end: weekEnd });
   173→    }).length;
   174→
   175→    // Recent requests (last 7 days created)
   176→    const sevenDaysAgo = addDays(now, -7);
   177→    const recentRequests = appointments.filter(apt => {
   178→      const createdAt = parseISO(apt.createdat);
   179→      return isWithinInterval(createdAt, { start: sevenDaysAgo, end: now });
   180→    }).length;
   181→
   182→    // Total customers
   183→    const totalCustomers = customers.length;
   184→
   185→    // Monthly revenue (sum of prices for this month's appointments)
   186→    const monthlyRevenue = enrichedAppointments
   187→      .filter(apt => {
   188→        if (!apt.fields.wunschtermin) return false;
   189→        const aptDate = parseISO(apt.fields.wunschtermin);
   190→        return isWithinInterval(aptDate, { start: monthStart, end: monthEnd });
   191→      })
   192→      .reduce((sum, apt) => sum + (apt.servicePrice || 0), 0);
   193→
   194→    return { thisWeekCount, recentRequests, totalCustomers, monthlyRevenue };
   195→  }, [enrichedAppointments, appointments, customers]);
   196→
   197→  // Current time for indicator
   198→  const [currentTime, setCurrentTime] = useState(new Date());
   199→  useEffect(() => {
   200→    const interval = setInterval(() => setCurrentTime(new Date()), 60000);
   201→    return () => clearInterval(interval);
   202→  }, []);
   203→
   204→  // Handle form submission
   205→  async function handleCreateAppointment(e: React.FormEvent) {
   206→    e.preventDefault();
   207→    setSubmitting(true);
   208→    try {
   209→      const wunschtermin = formData.wunschtermin_date && formData.wunschtermin_time
   210→        ? `${formData.wunschtermin_date}T${formData.wunschtermin_time}`
   211→        : undefined;
   212→
   213→      await LivingAppsService.createTerminanfrageEntry({
   214→        kunde_vorname: formData.kunde_vorname || undefined,
   215→        kunde_nachname: formData.kunde_nachname || undefined,
   216→        kunde_telefon: formData.kunde_telefon || undefined,
   217→        e_mail_adresse: formData.e_mail_adresse || undefined,
   218→        wunschtermin,
   219→        massageleistung: formData.massageleistung ? `https://my.living-apps.de/rest/apps/6913437daff7287a0f9bab21/records/${formData.massageleistung}` : undefined,
   220→        gesamtdauer: formData.gesamtdauer as Terminanfrage['fields']['gesamtdauer'] || undefined,
   221→        anmerkungen: formData.anmerkungen || undefined,
   222→      });
   223→
   224→      // Refresh data
   225→      const newAppointments = await LivingAppsService.getTerminanfrage();
   226→      setAppointments(newAppointments);
   227→      setFormData(initialFormData);
   228→      setCreateDialogOpen(false);
   229→    } catch (err) {
   230→      console.error('Failed to create appointment:', err);
   231→    } finally {
   232→      setSubmitting(false);
   233→    }
   234→  }
   235→
   236→  // Format currency
   237→  function formatCurrency(value: number | undefined): string {
   238→    if (value == null) return '-';
   239→    return new Intl.NumberFormat('de-DE', { style: 'currency', currency: 'EUR' }).format(value);
   240→  }
   241→
   242→  // Loading state
   243→  if (loading) {
   244→    return <LoadingState />;
   245→  }
   246→
   247→  // Error state
   248→  if (error) {
   249→    return (
   250→      <div className="min-h-screen bg-background p-4">
   251→        <div className="max-w-md mx-auto mt-20">
   252→          <Card className="border-destructive">
   253→            <CardContent className="pt-6 text-center">
   254→              <p className="text-destructive font-medium mb-2">Fehler beim Laden</p>
   255→              <p className="text-muted-foreground text-sm mb-4">{error.message}</p>
   256→              <Button onClick={() => window.location.reload()}>Erneut versuchen</Button>
   257→            </CardContent>
   258→          </Card>
   259→        </div>
   260→      </div>
   261→    );
   262→  }
   263→
   264→  return (
   265→    <div className="min-h-screen bg-background">
   266→      {/* Mobile Layout */}
   267→      <div className="lg:hidden">
   268→        {/* Header */}
   269→        <header className="px-4 pt-6 pb-4">
   270→          <h1 className="text-2xl font-semibold text-foreground">Termine</h1>
   271→          <p className="text-sm text-muted-foreground mt-1">
   272→            {format(new Date(), 'EEEE, d. MMMM yyyy', { locale: de })}
   273→          </p>
   274→        </header>
   275→
   276→        {/* Hero Section - Today's Schedule */}
   277→        <section className="px-4 pb-6">
   278→          <div className="flex items-center gap-2 mb-4">
   279→            <h2 className="text-lg font-semibold text-foreground">Heute</h2>
   280→            <Badge variant="secondary" className="rounded-full">
   281→              {todayAppointments.length} {todayAppointments.length === 1 ? 'Termin' : 'Termine'}
   282→            </Badge>
   283→          </div>
   284→
   285→          {todayAppointments.length === 0 ? (
   286→            <Card className="border-dashed">
   287→              <CardContent className="py-12 text-center">
   288→                <Calendar className="w-12 h-12 mx-auto text-muted-foreground/50 mb-4" />
   289→                <p className="text-muted-foreground font-medium">Keine Termine heute</p>
   290→                <p className="text-sm text-muted-foreground/70 mt-1">Genießen Sie Ihren freien Tag!</p>
   291→              </CardContent>
   292→            </Card>
   293→          ) : (
   294→            <div className="space-y-3">
   295→              {todayAppointments.map((apt, index) => {
   296→                const aptTime = apt.fields.wunschtermin ? parseISO(apt.fields.wunschtermin) : null;
   297→                const isPast = aptTime ? isBefore(aptTime, currentTime) : false;
   298→                const duration = apt.fields.gesamtdauer ? DURATION_MAP[apt.fields.gesamtdauer] : null;
   299→
   300→                return (
   301→                  <Card
   302→                    key={apt.record_id}
   303→                    className={`cursor-pointer transition-all hover:shadow-md ${isPast ? 'opacity-60' : ''}`}
   304→                    style={{ animationDelay: `${index * 50}ms` }}
   305→                    onClick={() => setSelectedAppointment(apt)}
   306→                  >
   307→                    <CardContent className="p-4">
   308→                      <div className="flex items-start justify-between">
   309→                        <div className="flex-1">
   310→                          <p className="text-lg font-bold text-primary">
   311→                            {aptTime ? format(aptTime, 'HH:mm', { locale: de }) : '-'}
   312→                          </p>
   313→                          <p className="font-semibold text-foreground mt-1">
   314→                            {apt.fields.kunde_vorname} {apt.fields.kunde_nachname}
   315→                          </p>
   316→                          <p className="text-sm text-muted-foreground">
   317→                            {apt.serviceName}
   318→                          </p>
   319→                        </div>
   320→                        {duration && (
   321→                          <Badge variant="outline" className="text-xs">
   322→                            {duration} Min
   323→                          </Badge>
   324→                        )}
   325→                      </div>
   326→                    </CardContent>
   327→                  </Card>
   328→                );
   329→              })}
   330→            </div>
   331→          )}
   332→        </section>
   333→
   334→        {/* Quick Stats Row - Horizontal Scroll */}
   335→        <section className="pb-6">
   336→          <ScrollArea className="w-full">
   337→            <ScrollAreaViewport>
   338→              <div className="flex gap-3 px-4">
   339→                <StatCardMobile
   340→                  icon={Calendar}
   341→                  value={stats.thisWeekCount}
   342→                  label="Diese Woche"
   343→                />
   344→                <StatCardMobile
   345→                  icon={Users}
   346→                  value={stats.totalCustomers}
   347→                  label="Kunden gesamt"
   348→                />
   349→                <StatCardMobile
   350→                  icon={Inbox}
   351→                  value={stats.recentRequests}
   352→                  label="Neue Anfragen"
   353→                  highlight={stats.recentRequests > 5}
   354→                />
   355→              </div>
   356→            </ScrollAreaViewport>
   357→          </ScrollArea>
